body {
  position: relative;
  background-color: #DDDDDD;
  font-family: "helvetica", sans-serif;
  font-weight: lighter;
  font-size: 14px;
  color: #555;
  margin: 0;
  padding: 0;
  min-width: 320px;
}

h1 {
  text-transform: uppercase;
  color: #333;
}

h3 {
  font-weight: lighter;
  color: #555555;
}

a {
  position: relative;
  color: #a8244f;
  text-decoration: none;
}
a:before {
  content: "";
  height: 2px;
  position: absolute;
  bottom: -5px;
  left: 0;
  right: 0;
  background-color: #7e1b3b;
  transform: rotateY(90deg);
  transition: transform 0.2s ease-in-out;
